bool myVar = true;

//Arithmetic Operators
int x = 5;
int y = 2;
int z = x + y; // Addition
z = x - y; // Subtraction
z = x * y; // Multiplication
z = x / y; // Division
z = x % y; // Modulus (remainder of division)

//Assignment Operators
int x = 5; // '=' is an assignment operator

//Comparison Operators
int x = 5;
int y = 2;
boolean z;

z = (x == y); // Equal to
z = (x != y); // Not equal to
z = (x > y); // Greater than
z = (x < y); // Less than
z = (x >= y); // Greater than or equal to
z = (x <= y); // Less than or equal to

//Logical Operators
bool x = true;
bool y = false;
bool z = x && y; // Logical AND
z = x || y; // Logical OR
z = !x; // Logical NOT

//String Declare Karein
std::string myVar = "Hello, World!";

//String Concatenate Karein
std::string str1 = "Hello, ";
std::string str2 = "World!";
std::string str3 = str1 + str2; // "Hello, World!"

//String Length Nikalein
std::string myVar = "Hello, World!";
int len = myVar.length(); // 13

//String Access Karein
std::string myVar = "Hello, World!";
char ch = myVar[0]; // 'H'

//Integer Input
int myVar;
std::cout << "Ek number daalein: ";
std::cin >> myVar;
std::cout << "Aapne daala: " << myVar;

//String Input
std::string myVar;
std::cout << "Kuch likhiye: ";
std::getline(std::cin, myVar);
std::cout << "Aapne likha: " << myVar;

//Multiple Inputs
int var1, var2;
std::cout << "Do numbers daalein: ";
std::cin >> var1 >> var2;
std::cout << "Aapne daale: " << var1 << " and " << var2;

//If Statement
int x = 5;
if (x > 0) {
    std::cout << "x is positive";
}

//Else Statement

int x = -5;
if (x > 0) {
    std::cout << "x is positive";
} else {
    std::cout << "x is not positive";
}

//Else If Statement
int x = 0;
if (x > 0) {
    std::cout << "x is positive";
} else if (x < 0) {
    std::cout << "x is negative";
} else {
    std::cout << "x is zero";
}
