//Arithmetic Operators
int x = 5;
int y = 2;
int z = x + y; // Addition
z = x - y; // Subtraction
z = x * y; // Multiplication
z = x / y; // Division
z = x % y; // Modulus (remainder of division)

//Assignment Operators
int x = 5; // '=' is an assignment operator

//Comparison Operators
int x = 5;
int y = 2;
boolean z;

z = (x == y); // Equal to
z = (x != y); // Not equal to
z = (x > y); // Greater than
z = (x < y); // Less than
z = (x >= y); // Greater than or equal to
z = (x <= y); // Less than or equal to

//Logical Operators
bool x = true;
bool y = false;
bool z = x && y; // Logical AND
z = x || y; // Logical OR
z = !x; // Logical NOT
