class Grandparent { // Base class
public:
    void gpFunc() {
        cout << "Function of Grandparent class." << endl;
    }
};

class Parent : public Grandparent { // Derived class
public:
    void pFunc() {
        cout << "Function of Parent class." << endl;
    }
};

class Child : public Parent { // Derived class
public:
    void cFunc() {
        cout << "Function of Child class." << endl;
    }
};


// Ab hum Child class ka object banakar in sabhi functions ko call kar sakte hain:
int main() {
    Child c;
    c.gpFunc(); // Output: Function of Grandparent class.
    c.pFunc(); // Output: Function of Parent class.
    c.cFunc(); // Output: Function of Child class.
    return 0;
}

