    return 0;
}

class Base { // Base class
public:
    void display() {
        cout << "Display function of Base class" << endl;
    }
};

class Derived : public Base { // Derived class
public:
    // Overriding display() function of Base class
    void display() {
        cout << "Display function of Derived class" << endl;
    }
};


// Ab hum in methods ko call kar sakte hain:
int main() {
    Base b;
    Derived d;

    b.display(); // Output: Display function of Base class
    d.display(); // Output: Display function of Derived class

