        ArrayList<Integer> list = new ArrayList<>();

        list.add(1);
        list.add(2);
        list.add(3);

        Iterator<Integer> iterator = list.iterator();

        while (iterator.hasNext()) {
            Integer next = iterator.next();

            System.out.println(next);

            if (next == 2) {
                iterator.remove();
            }
        }

        System.out.println("ArrayList after removing 2: " + list);
    }
}

