public class OperatorsDemo {
    public static void main(String[] args) {
        // Pre-Increment Operator
        int a = 5;
        System.out.println("Original value of a: " + a);
        ++a;
        System.out.println("After pre-increment, a is now: " + a);

        // Pre-Decrement Operator
        int b = 10;
        System.out.println("Original value of b: " + b);
        --b;
        System.out.println("After pre-decrement, b is now: " + b);

        // Addition Operator
        int num1 = 5;
        int num2 = 10;
        int sum = num1 + num2;
        System.out.println("Sum of " + num1 + " and " + num2 + " is: " + sum);

        // Subtraction Operator
        int x = 15;
        int y = 10;
        int difference = x - y;
        System.out.println("Difference of " + x + " and " + y + " is: " + difference);

        // Multiplication Operator
        int p = 4;
        int q = 5;
        int product = p * q;
        System.out.println("Product of " + p + " and " + q + " is: " + product);

        // Division Operator
        int dividend = 20;
        int divisor = 4;
        int quotient = dividend / divisor;
        System.out.println("Quotient of " + dividend + " divided by " + divisor + " is: " + quotient);

        // Modulus Operator
        int dividendMod = 20;
        int divisorMod = 3;
        int remainder = dividendMod % divisorMod;
        System.out.println("Remainder of " + dividendMod + " divided by " + divisorMod + " is: " + remainder);

        // Post-Increment Operator
        int num = 5;
        int incrementedNum = num++;
        System.out.println("Original value of num: " + num);
        System.out.println("Value of incrementedNum: " + incrementedNum);

        // Post-Decrement Operator
        int numDec = 10;
        int decrementedNum = numDec--;
        System.out.println("Original value of numDec: " + numDec);
        System.out.println("Value of decrementedNum: " + decrementedNum);
    }
}

// Widening Type Casting
public class WideningTypeCasting {
    public static void main(String[] args) {
        int num = 10;
        System.out.println("The integer value: " + num);

        double data = num;
        System.out.println("The double value: " + data);
    }
}

// Narrowing Type Casting
public class NarrowingTypeCasting {
    public static void main(String[] args) {
        double num = 10.99;
        System.out.println("The double value: " + num);

        int data = (int)num;
        System.out.println("The integer value: " + data);
    }
}

// immutable object
public class ImmutableObjectExample {
    public static void main(String[] args) {
        String data = "Hello, World!";
        data = data.replace("World", "Java");
        System.out.println("The modified string is: " + data);
    }
}

// sequence
public class SequenceExample {
    public static void main(String[] args) {
        String data = "Hello, World!";
        System.out.println("The first character of the string is: " + data.charAt(0));
        System.out.println("The last character of the string is: " + data.charAt(data.length() - 1));
    }
}

// object
public class ObjectExample {
    public static void main(String[] args) {
        String data = "Hello, World!";
        System.out.println("The length of the string is: " + data.length());
        System.out.println("The lower case of the string is: " + data.toLowerCase());
        System.out.println("The upper case of the string is: " + data.toUpperCase());
    }
}
